import {useState,useRef} from "react"



function App() {
  const [count, setCount] = useState(0);



  const add_mes = () => {
    if(count === "Hello World"){
      setCount("Bye");
    }else{
      setCount("Hello World");
    }


  }; 


  return (
    <div>
      <div>
        {count}
      </div>
      <input type="text" value = "メッセージ"/>

    </div>
  );
}

export default App;
