import './App.css';
import {useState,useRef} from "react"



function App() {
  const [count, setCount] = useState(0);

  const nameRef = useRef();

  const add_mes = () => {
    const name = nameRef.current.value;
    useState(name)
  }; 


  return (
    <div>
      <div>
        {count}
      </div>
      <input type="text" value = "メッセージ" ref={nameRef}/>
      <button onClick={add_mes}>タスクを追加</button>
    </div>
  );
}

export default App;
