import './App.css';

function App() {
  const handleAddTodo = () => {
    const name = todoNameRef.current.value;
    if (name === "") return;
    setTodos((prevTodos) => {
        return(
            [...prevTodos,{id:uuidv4(),name:name,completed:false}]
        );
    })
    todoNameRef.current.value = null;
};
  return (
    <div >
      <div>Hello</div>
      <input type="text"/>
      <button onClick={handleAddTodo}>送信</button>
    </div>
  );
}

export default App;
