import './App.css';
import {useState,useRef} from "react"



function App() {
  const [count, setCount] = useState(0);

  const nameRef = useRef();

  const add_mes = () => {
    if(count === "Hello World"){
      setCount("Bye");
    }else{
      setCount("Hello World");
    }


  }; 


  return (
    <div>
      <div>
        {nameRef}
      </div>
      <input type="text" value = "メッセージ" ref={nameRef}/>
      <button onClick={add_mes}>タスクを追加</button>
    </div>
  );
}

export default App;
